import { useSelector } from "react-redux";
import "./_cart.scss";

const Cart = () => {
  const cart = useSelector((state) => state.cr);
  return (
    <div>{cart.cartItems.length === 0 ? <EmptyCart /> : <FilledCart />}</div>
  );
};

export default Cart;
